//pega os dados do "body" do input
let data = $input.item.json.actions;

//faz o loop sob cada uma das propriedades
for (i in data){
  //pega a propriedade atual, o seu "action" e o seu "value"
  let dataItem = data[i];
  let action = dataItem.action_type;
  let value = dataItem.value;

  //cria uma nova propriedade no input com o nome da "action" e o valor do "value"
  $input.item.json[action] = value
}

//retorna o input com os novos campos
return $input.item;

